command.script:
#Requires -Version 3
$InputData = [PSCustomObject]@{ RequestNumber = "REQ12345" }
$OutputData = [PSCustomObject]@{ Mailbox = "joe.bloggs@test.com" MailboxGivenName = "joe.bloggs" Owner = "steve.walker@test.com" Output1 = "Result1" Array = @("Test1", "Test2") Output2 = "Result2" Retry = 0 ScriptExitText = "" }
$AOResults =[PSCustomObject]@{ Inputs = $InputData Outputs = $OutputData }
$OutputJSON = $AOResults | ConvertTo-Json -Compress
Write-Host "$($OutputJSON)"
exit 0
